import { Component, OnInit } from '@angular/core';
import { ActivatedRoute,Router } from '@angular/router';
import { User } from '../model/user';
import { UserCrudService } from '../servizi/user-crud.service';

@Component({
  selector: 'app-form-user',
  templateUrl: './form-user.component.html',
  styleUrls: ['./form-user.component.css']
})
export class FormUserComponent implements OnInit{

  message!: string;
  user: User = new User();
  users:User[]=[];
  idUser!:number;

  constructor(private crudUser: UserCrudService,private activatedRoute: ActivatedRoute, private router: Router){}
  ngOnInit(): void {
    this.idUser = this.activatedRoute.snapshot.params ['id'];
    this.crudUser.getUser(this.idUser).subscribe((user:User) =>{
      this.user = user;
    });
  }
  insertUser(user: User): void {
    this.crudUser.addUser(user).subscribe((message: string) => {
      this.message = message;
    });
  }

  getUser(id: number): any{
    this.crudUser.getUser(id).subscribe(( user: User) => {this.user = user})
  }

  onSearch(): void {
    this.user = this.getUser(this.user.id);
  }

  resetMessage(): void{
    this.message = "";
  }

  onSubmit(): void {
    if(this.users.length==null){
      this.crudUser.addUser(this.user).subscribe((message:string)=>{
        this.message=message;
        this.router.navigate(["tabellaUser"]);
      });
      this.user = new User();
    }
    else{
      this.crudUser.addUser(this.user).subscribe((message:string)=>{
        this.message=message;
        this.router.navigate(["tabellaUser"]);
      })
      this.user = new User();
    }

  }

}
<nav class="navbar navbar-expand-lg navbar-light bg-light">
    <a class="navbar-brand" href="#">System Feedback Fiven</a>
    <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
      <div class="navbar-nav">
        <a class="nav-item nav-link" routerLink="">Home </a>
        <a class="nav-item nav-link active" href="formUser/:id">Form User <span class="sr-only">(current)</span></a>
        <a class="nav-item nav-link" href="formTag/:id">Form Tag</a>
        <a class="nav-item nav-link" href="tabellaTag">Tabella Tag</a>
        <a class="nav-item nav-link" href="formFeed/:id">Form Feedback</a>
        <a class="nav-item nav-link" href="tabellaFeed">Tabella Feedback</a>
      </div>
    </div>
  </nav>
  <form #form="ngForm" (ngSubmit)="onSubmit()">
    <div class="container-fluid" class="inputWidth">
      <h3>Registrazione User</h3>
      <br>
      <div class="login">
        <div class="container">
          <div class="row">
            <div class="w-100"></div>
            <div class="col"> <label for="username" class="form-label pt-3">Username</label>
              <input type="text" class="form-control" name="username" id="username" placeholder="Username: "
                onclick="resetMessage()" [(ngModel)]="user.username">
            </div>
            <div class="col"> <label for="password" class="form-label pt-3">Password</label>
              <input type="text" class="form-control" name="password" id="password" placeholder="Password: "
                onclick="resetMessage()" [(ngModel)]="user.password">
            </div>
          </div>
        </div>
      </div>

      <div class="login">
        <div class="container">
          <div class="row">
            <div class="w-100"></div>
            <div class="col"> <label for="email" class="form-label pt-3">Email</label>
              <input type="email" class="form-control" name="email" id="email" onclick="resetMessage()"
                [(ngModel)]="user.email">
            </div>
            <div class="col"> <label for="name" class="form-label pt-3">Name</label>
              <input type="text" class="form-control" name="name" id="name" placeholder="Name: "
                onclick="resetMessage()" [(ngModel)]="user.name">
            </div>
          </div>
        </div>
      </div>
      <div class="login">
        <div class="container">
          <div class="row">
            <div class="w-100"></div>
            <div class="col"> <label for="surname" class="form-label pt-3">Surname</label>
              <input type="text" class="form-control" name="surname" id="surname" placeholder="Surname: "
                onclick="resetMessage()" [(ngModel)]="user.surname">
            </div>
            <div class="col"> <label for="birthDate" class="form-label pt-3">Birthdate</label>
              <input type="date" class="form-control" name="birthDate" id="birthDate" placeholder="Birthdate: "
                onclick="resetMessage()" [(ngModel)]="user.birth_date">
            </div>
          </div>
        </div>
      </div>

      <div class="login">
        <div class="container">
          <div class="row">
            <div class="w-100"></div>
            <div class="col"> <label for="birthPlace" class="form-label pt-3">Birthplace</label>
              <input type="text" class="form-control" name="birthPlace" id="birthPlace" onclick="resetMessage()"
                [(ngModel)]="user.birth_place">
            </div>
            <div class="col"> <label for="cf" class="form-label pt-3">Fiscal Code</label>
              <input type="text" class="form-control" name="cf" id="cf" placeholder="Inserisci il codice fiscale"
                onclick="resetMessage()" [(ngModel)]="user.cf">
            </div>
          </div>
        </div>
      </div>



        <div *ngIf="user.id!=0">
        <label for="enableTrue">Enable</label >
          <input style="margin-left: 2%;" type="radio" id="enableTrue" name="enable" value="true" [(ngModel)]="user.enable">
          <label style="margin-left: 4%;" for="enableFalse">Disable</label>
          <input  style="margin-left: 2%;" type="radio" id="enableFalse" name="enable" value="false" [(ngModel)]="user.enable">
        </div>
          <br>
        <button type="submit" class="btn btn-dark">Salva</button>
        </div>
  </form>
