import { Component } from "@angular/core";
import { User } from "../model/user";
import { UserCrudService } from "../servizi/user-crud.service";
import { Role } from "../model/role";

@Component({
  selector: "app-tab-user",
  templateUrl: "./tab-user.component.html",
  styleUrls: ["./tab-user.component.css"],
})
export class TabUserComponent {
  users: User[] = [];
  message!: string;
  token!: string;
  page!: number;
  nPagine!: number;
  role!:Role;
  roles:Role[]=[];

  constructor(private crud: UserCrudService) {}

  ngOnInit(): void {
    this.page = 0;
    this.getUserImp(this.page);
  }

  getUser(): void {
    this.crud.getUsers(this.page).subscribe((users: User[]) => {
      this.users = users;
    });
  }
  getUserImp(page: number): void {
    this.crud.getUsersDim().subscribe((dim: number) => {
      this.nPagine = Math.round(dim / 5);
    });
    if (page < 0) {
      this.page = 0;
    } else if (page > this.nPagine) {
      this.page = this.nPagine;
    } else {
      this.page = page;
    }
    this.crud.getUsers(this.page).subscribe((users: User[]) => {
      this.users = users;
      this.message = "";
    });
    if (this.users.length == 0) {
      this.nPagine = this.nPagine - 1;
    }
  }

  deleteUser(user: User): void {
    let confirmation = confirm("Conferma la cancellazione dell'utente?");
    if (confirmation) {
      this.crud
        .deleteUser(user.id)
        .subscribe((message: string) => {
          this.message = message;
          this.getUserImp(this.page);
        });
    }
  }
}
<nav class="navbar navbar-expand-lg navbar-dark bg-primary">
    <a class="navbar-brand" href="#">System Feedback Fiven</a>
    <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup"
        aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
        <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
        <div class="navbar-nav">
            <a class="nav-item nav-link" routerLink="">Home </a>
            <a class="nav-item nav-link" href="formUser/:id">Form User</a>
            <a class="nav-item nav-link" href="formTag/:id">Form Tag</a>
            <a class="nav-item nav-link" href="tabellaTag">Tabella Tag</a>
            <a class="nav-item nav-link" href="formFeed/:id">Form Feedback</a>
            <a class="nav-item nav-link" href="tabellaFeed">Tabella Feedback</a>
            <a class="nav-item nav-link active" href="tabellaUser">Tabella User <span
                    class="sr-only">(current)</span></a>
        </div>
    </div>
</nav>
<div class="container p-3 bg-body-secondary rounded main centro mt-5 mb-5 rounded-5" *ngIf="users.length > 0">
    <section class="container pb-5">
        <div class="d-flex justify-content-between">
            <h3>Tabella User</h3>
            <a routerLink="/formUser/:id" class="btn btn-primary">Inserisci user</a>
        </div>
        <div class="table-responsive">
            <table class="table table-striped table-bordered">
                <tr class="bg-primary text-white">
                    <th>Id</th>
                    <th>Username</th>
                    <th>Password</th>
                    <th>Email</th>
                    <th>Name</th>
                    <th>Surname</th>
                    <th>Birthdate</th>
                    <th>Birthplace</th>
                    <th>Fiscal Code</th>
                    <th>Role</th>
                    <th>Modifica</th>
                    <th>Rimuovi</th>
                </tr>
                <tr *ngFor="let u of users">
                    <td>{{u.id}}</td>
                    <td>{{u.username}}</td>
                    <td>{{u.password}}</td>
                    <td>{{u.email}}</td>
                    <td>{{u.name}}</td>
                    <td>{{u.surname}}</td>
                    <td>{{u.birthDate}}</td>
                    <td>{{u.birthPlace}}</td>
                    <td>{{u.cf}}</td>
                    <td>{{u.roleName}}</td>
                    <td>
                        <button class="btn btn-warning" routerLink='/formUser/{{u.id}}'>
                            <i class="fas fa-edit"></i> Modifica
                        </button>
                    </td>
                    <td>
                        <button class="btn btn-danger" (click)="deleteUser(u)">
                            <i class="fas fa-trash-alt"></i> Cancella
                        </button>
                    </td>
                </tr>
            </table>
        </div>
    </section>
    <div class="d-flex justify-content-center my-3">
        <a class="btn btn-outline-warning px-3" (click)="getUserImp(0)">First page</a>
        <a class="btn btn-outline-warning px-3" (click)="getUserImp(page-1)">Previous</a>
        <a class="btn btn-outline-dark px-3 bg-warning">Pagina {{page+1}}</a>
        <a class="btn btn-outline-warning px-3" (click)="getUserImp(page+1)">Next</a>
        <a class="btn btn-outline-warning px-3" (click)="getUserImp(nPagine)">Last page</a>
    </div>
</div>
