import { Component, OnInit} from '@angular/core';
import { Feedback } from '../model/feedback';
import { FeedbackService } from '../servizi/feedback.service';
import { loginService } from '../servizi/login-service';
import { TokenJWT } from '../model/TokenJWT';

@Component({
  selector: 'app-tab-feedback',
  templateUrl: './tab-feedback.component.html',
  styleUrls: ['./tab-feedback.component.css']
})
export class TabFeedbackComponent implements OnInit{
  feedbacks: Feedback[] = [];
  message!: string;
  token!: TokenJWT;
  page!:number;
  nPagine!:number;
  

  constructor(private crud: FeedbackService, private auth: loginService){}


  ngOnInit(): void {
    this.token = this.auth.token;
    this.page = 0;
    this.getFeedImp(this.page);
  }

  getFeedbacks(): void {
    this.crud.getFeedbacks(this.page).subscribe((feedbacks: Feedback[]) => {
      this.feedbacks = feedbacks;
    });
  }
  
  getFeedImp(page:number): void{
    this.crud.getFeedbacksdim().subscribe((dim:number)=>{
      this.nPagine = Math.round(dim/5);
    })
    if(page<0){
      this.page = 0;
    }
    else if(page>this.nPagine){
      this.page = this.nPagine;
    }
    else{
      this.page = page;
    }
    this.crud.getFeedbacks(this.page).subscribe((feedbacks:Feedback[])=>{
      this.feedbacks = feedbacks;
      this.message = "";
    })
    if(this.feedbacks.length==0){
      this.nPagine = this.nPagine-1;
    }
  }
  
}
<div class="container p-3 bg-body-secondary rounded main centro mt-5 mb-5 rounded-5">
  <div class="row">
    <div class="d-flex justify-content-between" >

      <nav class="navbar navbar-dark bg-primary">
        <h3>Feedback</h3>
        <a class="btn btn-primary" routerLink="/formFeed/:id">Inserisci Feedback</a>
      </nav>
      <br> <br>
    </div>
    
    <!-- Feedback Inviati -->
    <div class="col-12 col-md-6">
      <section class="container pb-5">
        <div class="d-flex justify-content-between">
          <h3>Feedback Inviati <i class="fa fa-paper-plane-o" style="font-size:24px"></i></h3>
        </div>
        <div class="d-flex justify-content-center my-3">
          <div class="table-responsive">
            <table class="table table-striped">
              <!-- Intestazione tabella -->
              <tr class="bg-primary">
                <th class="text-white">#</th>
                <th class="text-white">Inviato il</th>
                <th class="text-white">Gruppo di lavoro</th>
                <th class="text-white">Esito valutazione</th>
                <th class="text-white">Inviato a</th>
                <th></th>
              </tr>
              <!-- Dati tabella -->
              <tbody *ngFor="let f of feedbacks">
                <tr *ngIf="f.sentBy==token.username">
                  <td>{{f.id}}</td>
                  <td>{{f.sendDate}}</td>
                  <td>{{f.teamGroup}}</td>
                  <td>
                    <ng-container *ngIf="f.feedbackType=='Positivo'">
                      <i class="fa fa-thumbs-o-up" style="font-size:24px"></i>{{f.feedbackType}}
                    </ng-container>
                    <ng-container *ngIf="f.feedbackType=='Miglioramento'">
                      <i class="fa fa-cog fa-spin"> </i>{{f.feedbackType}}
                    </ng-container>
                  </td>
                  <td>{{f.sentAt}}</td>
                  <td>
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="var(--bs-primary)" class="bi bi-eye-fill" viewBox="0 0 16 16">
                      <path class="eye-open" d="M10.5 8a2.5 2.5 0 1 1-5 0 2.5 2.5 0 0 1 5 0z"/>
                      <path class="eye-closed" d="M0 8s3-5.5 8-5.5S16 8 16 8s-3 5.5-8 5.5S0 8 0 8zm8 3.5a3.5 3.5 0 1 0 0-7 3.5 3.5 0 0 0 0 7z"/>
                    </svg>
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </section>
    </div>
    
    <!-- Feedback Ricevuti -->
    <div class="col-12 col-md-6">
      <section class="container pb-5">
        <div class="d-flex justify-content-between">
          <h3>Feedback Ricevuti <i class="fa fa-paper-plane-o" style="font-size:24px;transform: rotate(180deg);"></i></h3>
        </div>
        <div class="d-flex justify-content-center my-3">
          <div class="table-responsive">
            <!-- Tabelle Feedback Ricevuti -->
            <table class="table table-striped">
              <tr class="bg-primary">
                <th class="text-white">#</th>
                <th class="text-white">Inviato il</th>
                <th class="text-white">Gruppo di lavoro</th>
                <th class="text-white">Esito valutazione</th>
                <th class="text-white">Inviato a</th>
                <th></th>
              </tr>
              <!-- Dati tabella -->
              <tbody *ngFor="let f of feedbacks">
                <tr *ngIf="f.sentAt==token.username">
                  <td>{{f.id}}</td>
                  <td>{{f.sendDate}}</td>
                  <td>{{f.teamGroup}}</td>
                  <td>
                    <ng-container *ngIf="f.feedbackType=='Positivo'">
                      <i class="fa fa-thumbs-o-up" style="font-size:24px"></i>{{f.feedbackType}}
                    </ng-container>
                    <ng-container *ngIf="f.feedbackType=='Miglioramento'">
                      <i class="fa fa-cog fa-spin"> </i>{{f.feedbackType}}
                    </ng-container>
                  </td>
                  <td>{{f.sentAt}}</td>
                  <td>
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="var(--bs-primary)" class="bi bi-eye-fill" viewBox="0 0 16 16">
                      <path class="eye-open" d="M10.5 8a2.5 2.5 0 1 1-5 0 2.5 2.5 0 0 1 5 0z"/>
                      <path class="eye-closed" d="M0 8s3-5.5 8-5.5S16 8 16 8s-3 5.5-8 5.5S0 8 0 8zm8 3.5a3.5 3.5 0 1 0 0-7 3.5 3.5 0 0 0 0 7z"/>
                    </svg>
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </section>
    </div>
  </div>
</div>
